package com.android.customview.viewsimport android.content.Contextimport android.graphics.Colorimport android.graphics.Rectimport android.util.AttributeSetimport android.view.View/** * Description : 自定义电池控件 * * Author     : TAN * * Time       : 2023/7/18 13:49 */class BatteryMeterView @JvmOverloads constructor(context: Context, attrs: AttributeSet? = null, defStyleAttr: Int = 0) : View(context, attrs, defStyleAttr) {    companion object {        /** 默认充电状态*/        private const val DEFAULT_CHARGING_STATE = false        /**默认电池电量*/        private const val DEFAULT_BATTERY_LEVEL = 70        /**默认警告级别*/        private const val DEFAULT_WARNING_LEVEL = 30        /**默认电池电量颜色*/        private const val DEFAULT_BATTERY_LEVEL_COLOR = Color.GREEN        /**默认警告颜色*/        private const val DEFAULT_WARNING_COLOR = Color.RED        /**默认背景颜色*/        private const val DEFAULT_BACKGROUND_COLOR = Color.LTGRAY        /**默认电池头颜色*/        private const val DEFAULT_BATTERY_HEAD_COLOR = Color.DKGRAY        /**默认文本颜色*/        private const val DEFAULT_TEXT_COLOR = Color.DKGRAY        /**默认充电颜色*/        private const val DEFAULT_CHARGING_COLOR = Color.DKGRAY        /**文字大小比例*/        private const val TEXT_SIZE_RATIO = 0.5f    }    //电池尺寸    private var contentHeight = 0    private var contentWidth = 0    private var batteryHeadWidth = 0    private var mainContentOffset = 20    //形状    private val backgroundRect = Rect()    private val batteryLevelRect = Rect()}