package com.android.data;import androidx.databinding.BaseObservable;import androidx.databinding.Bindable;import com.android.custom.views.BR;public class AttributeObservable extends BaseObservable {    private String text;    private int textColor;    private int textSize;    private int style;    private int background;    public AttributeObservable(String text , int textColor, int textSize, int textStyle, int background) {        this.text = text;        this.textColor = textColor;        this.textSize = textSize;        this.style = textStyle;        this.background = background;    }    public AttributeObservable() {    }    @Bindable    public String getText() {        return text;    }    public void setText(String text) {        this.text = text;        notifyPropertyChanged(BR.text);    }    @Bindable    public int getTextColor() {        return textColor;    }    public void setTextColor(int textColor) {        this.textColor = textColor;        notifyPropertyChanged(BR.textColor);    }    @Bindable    public int getTextSize() {        return textSize;    }    public void setTextSize(int textSize) {        this.textSize = textSize;        notifyPropertyChanged(BR.textSize);    }    @Bindable    public int getStyle() {        return style;    }    public void setStyle(int style) {        this.style = style;        notifyPropertyChanged(BR.style);    }    @Bindable    public int getBackground() {        return background;    }    public void setBackground(int background) {        this.background = background;        notifyPropertyChanged(BR.background);    }}