package com.android.dataimport androidx.databinding.BaseObservableimport androidx.databinding.ObservableFieldclass AttributeObservable {    private val textField: ObservableField<String> = ObservableField()    /**Textview text*/    var text: String? = textField.get()        set(value) = textField.set(value)    private val textColorField: ObservableField<Int> = ObservableField()    /**TextView text color*/    var textColor: Int? = textColorField.get()        set(value) = textColorField.set(value)    private val textSizeField: ObservableField<Int> = ObservableField()    /**TextView text size*/    var textSize: Int? = textSizeField.get()        set(value) = textSizeField.set(value)}