package com.android.viewsimport android.content.Contextimport android.graphics.Canvasimport android.graphics.Colorimport android.graphics.LinearGradientimport android.graphics.Paintimport android.graphics.Shaderimport android.util.AttributeSetimport android.view.Viewimport androidx.recyclerview.widget.LinearSnapHelperclass CircleView(private val context: Context, private val attributeSet: AttributeSet?, private val defStyle: Int) : View(context, attributeSet, defStyle) {    private val circlePaint: Paint = Paint(Paint.ANTI_ALIAS_FLAG)    var radius: Float = 0f    init {        val shader: Shader = LinearGradient(100f, 100f, 500f, 500f, Color.parseColor("#E91E63"), Color.parseColor("#2196F3"), Shader.TileMode.CLAMP)        circlePaint.apply {            setShader(shader)        }    }    override fun onSizeChanged(w: Int, h: Int, oldw: Int, oldh: Int) {        super.onSizeChanged(w, h, oldw, oldh)        radius = if (w > h) {            h / 2f        } else {            w / 2f        }    }    override fun onDraw(canvas: Canvas?) {        canvas?.apply {            drawCircle(width / 2f, height / 2f, radius, circlePaint)        }    }}